<template>
  <div class="app-page">
    <div id="container">
      <div class="img-container" ref="phoneBg">
        <img
          ref="phone"
          class="phone"
          src="data:image/svg+xml;base64,"
          alt="isometric phone"
          width="600"
        />
      </div>
      <div id="trigger"><h3>Try scrolling the page.</h3></div>
      <button @click="animate()">animate</button>
    </div>
  </div>
</template>

<script>
import ScrollMagic from "scrollmagic";
import gsap from "gsap/all";

export default {
  data() {
    return {
      controller: null,
      tween: null,
      scene: null
    };
  },
  mounted() {
    // this.animate();
  },
  methods: {
    animate() {
        this.controller = new ScrollMagic.Controller();
        const { phone, phoneBg } = this.$refs;
        this.tween = gsap.timeline();

        this.tween.fromTo(
        phone,
        1.5,
        { autoAlpha: 0, y: 200 },
        { autoAlpha: 1, y: 100, ease: "power3.inOut" }
      );
      this.tween.fromTo(phoneBg, 0.75, { autoAlpha: 0, x: -100 }, { autoAlpha: 1, x: 0, ease: "power3.inOut"}, "-=1.2");
      this.scene = new ScrollMagic.Scene({
        triggerElement: "#trigger",
        triggerHook: 100
      })
        .setTween(this.tween)
        .addTo(this.controller);
    },

    runAnim() {
      const { phone } = this.$refs;
      gsap.fromTo(
        phone,
        1.5,
        { autoAlpha: 0, y: 200 },
        { autoAlpha: 1, y: 0, ease: "power3" }
      );
    }
  },
}
</script>


<style lang="scss">
  .container {
    height: 300vh;
  }

  .img-container {
    clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
    clip-path: polygon(25% 0%, 100% 0%, 75% 100%, 0% 100%);
    position: relative;
    top: 60vh;
    background: #ffe6c5;
    width: 450px;
    height: 600px;
    margin: 15px 0;
    padding: 15px;
    overflow: hidden;
  }

  .phone {
    position: relative;
    left: 20px;
    transform: scale(1);
  }

  #trigger {
    position: absolute;
    top: 110;
  }
</style>